import { createStore } from 'vuex'

export default createStore({
  state: {
    day: 11,
    month: 4,
    year: 2022,
    hour: 10,
    minutes: 15,
  },
  getters: {
    formattedDate(state) {
      return `${state.day}/${state.month}/${state.year}`
    },
    formattedTime(state) {
      return `${state.hour}h${state.minutes}`
    }
  },
  mutations: {
    INCREMENT_MONTH(state) {
      state.month++
    }
  },
  actions: {
    incrementMonth(context) {
      context.commit("INCREMENT_MONTH")
    }
  },
  modules: {
  }
})
