import { createStore } from "vuex";
import createPersistedState from "vuex-persistedstate";

export default createStore({
  state: {
    sessionToken: null,
    userId: null,
    shares: [],
  },
  getters: {
  },
  mutations: {
    addShare (state, share) {
      state.shares.push(share)
    }
  },
  actions: {
  },
  modules: {},
  plugins: [createPersistedState()],
});
