import { createStore } from "vuex";

export default createStore({
  state: {
    sessionToken: null,
    userId: null,
    shares: [],
  },
  getters: {
    reverseShares: state => {
      return state.shares.slice().reverse();
    }
  },
  mutations: {
    addShare (state, share) {
      state.shares.push(share)
    }
  },
  actions: {
  },
  modules: {},
});
